mapper.insert:
		insert into member (
      member_id, member_pw, member_name, member_email, member_tel, member_zipcode, member_addr,member_addr2, member_regdate
    )
    values (
      #{member_id, jdbcType=VARCHAR}, #{member_pw}, #{member_name}, #{member_email}, #{member_tel}, #{member_zipcode},#{member_addr}, #{member_addr2}, SYSDATE
    )

mapper.select:
select *
from member
where member_id=#{member_id, jdbcType=VARCHAR}

		select *
    from member
    where member_id=#{member_id, jdbcType=VARCHAR} and member_pw=#{member_pw}

mapper.update:
		update member set
    member_id=#{member_id, jdbcType=VARCHAR}, member_pw=#{member_pw}, member_name=#{member_name},
		member_email=#{member_email}, member_tel=#{member_tel}, member_zipcode=#{member_zipcode}, member_addr=#{member_addr}, member_addr2=#{member_addr2}
    where member_id=#{member_id, jdbcType=VARCHAR}

mapper.select:
		select * from member
    where member_name = #{member_name}
    and member_tel = #{member_tel}

		select * from member
    where member_id = #{member_id, jdbcType=VARCHAR}
    and member_tel = #{member_tel} and member_name = #{member_name}
