mapper.insert:
insert into product (
  product_number, product_name, product_detail, product_price, product_stock, product_company, product_image, product_regdate
)
values (
  #{product_number}, #{product_name}, #{product_detail}, #{product_price}, #{product_stock}, #{product_company}, #{product_image}, SYSDATE
)

mapper.update:
update product
set product_step = product_step + 1
where product_group = #{product_group} and product_step > #{product_step}

mapper.select:
select *
from (
  select b.*, ROWNUM rnum
  from (
    select *
    from product
    order by product_group desc, product_step asc
  ) b where ROWNUM <= #{start} + #{count}
)
where rnum > #{start}

SELECT *
FROM (
  SELECT b.*, ROWNUM rnum
  FROM (
    SELECT *
    FROM product
    WHERE ${columnParam} LIKE '%' || #{keyWord} || '%'
    ORDER BY product_group DESC, product_step ASC
  ) b WHERE ROWNUM <= #{start} + #{count}
) WHERE rnum > #{start}

mapper.update:
update product
set product_name = #{product_name}, product_detail = #{product_detail}, product_price = #{product_price}, product_stock = #{product_stock}, product_company = #{product_company}, product_image = #{product_image}
where product_number = #{product_number}
