mapper.insert:
insert into qna (
  qna_number, qna_writer, qna_pw, qna_title, qna_content, qna_regdate, qna_ref, qna_step,qna_level
)
values (
  #{qna_number}, #{qna_writer},# {qna_pw}, #{qna_title}, #{qna_content}, SYSDATE, #{qna_ref}, #{qna_step}, #{qna_level}
)

mapper.update:
update qna
set qna_step = qna_step + 1
where qna_ref = #{qna_ref} and qna_step > #{qna_step}

mapper.select:
select *
from (
  select q.*, ROWNUM rnum
  from (
    select *
    from qna
    order by qna_ref desc, qna_step asc
  ) q where ROWNUM <= #{start} + #{count}
)
where rnum > #{start}

SELECT *
FROM (
  SELECT qna.*, ROWNUM rnum
  FROM (
    SELECT *
    FROM qna
    WHERE ${columnParam} LIKE '%' || #{keyWord} || '%'
    ORDER BY qna_number DESC
  ) qna
  WHERE ROWNUM <= #{start} + #{count}
)
WHERE rnum > #{start}

mapper.update:
update qna
set qna_readcount = qna_readcount + 1
where qna_number = #{qna_number}

update qna
set qna_writer = #{qna_writer}, qna_title = #{qna_title}, qna_content = #{qna_content}
where qna_number = #{qna_number} and qna_pw = #{qna_pw}
